package aoc

class Day01 {
    companion object {
        fun part1(input: List<String>): Int {
            var count = 1
            
            for (i in 1 until input.size) {
                if (input[i] > input[i-1]) {
                    count++
                }
            }

            return count
        }

        fun part2(input: List<String>): Int {
            return input.size
        }
    }
    
}

fun main() {
    val input = readInput("Day01")
    println(Day01.part1(input))
    println(Day01.part2(input))
}
